mapper.select:


	select ano, location, title, content, writer, replycnt, hit, regdate, updatedate, startdate, enddate 
		from(
    		select /*+index_desc(album pk_album)*/
        		rownum rn, ano, location, title, content, writer, replycnt, hit, regdate, updatedate, startdate, enddate 
    		from album 
    		where 



    		rownum<=#{pageNum}*#{amount} 
		) 
	where rn>(#{pageNum}-1)*#{amount}
