mapper.select:

select rno, bno, reply, replyer, replydate, updatedate 
from (
	select /*+INDEX(reply idx_board_reply)*/
		rownum rn, rno, bno, reply, replyer, replydate, updatedate
		from board_reply where bno=#{bno} and rno>0 and rownum <= #{cri.pageNum} * #{cri.amount} 
) where rn > (#{cri.pageNum} -1) * #{cri.amount}


select rno, bno, reply, replyer, replydate, updatedate 
from (
	select /*+INDEX_desc(reply idx_board_reply)*/
		rownum rn, rno, bno, reply, replyer, replydate, updatedate
		from board_reply where rno>0 and rownum <= #{pageNum} * #{amount} order by replydate desc
) where rn > (#{pageNum} -1) * #{amount}
